import React, { useState } from 'react';
import {Navigation} from '../components/Navigation/Navigation';
import {Header} from '../components/Header/Header';

import { Input } from '../components/Input/Input';
import {TodoItem} from '../components/TodoItem/TodoItem';
import NoContent from '../assets/icons/todayTask.png';

import { ITodoItem } from '../types/types';
import './style.css';

export const MainPage = () => {

    const [inputStr, setInputStr] = useState('');
    const [todoList, setTodoList] = useState<ITodoItem[]>([]);

    const handleInputChange = (value: string) => {
        setInputStr(value);
      };

    const handlePressEnter = (e: any) => {
        const value = e.target.value;
        if (!value.trim()) return;
        setTodoList(prev => [
            ...prev,
            {
                id: Math.random(),
                title: value,
                isDone: false,
            },
        ]);
        setInputStr('');
    };

    const handleChangeTodo = (item: ITodoItem) => {
        const indexTodo = todoList.findIndex(todo => item.id === todo.id);

        const newTodoItem = {
            id: item.id,
            title: item.title,
            isDone: !item.isDone,
        };

        setTodoList(prev => {
            const newTodoList = [
                ...prev.slice(0, indexTodo),
                newTodoItem,
                ...prev.slice(indexTodo + 1),
            ];
            return newTodoList;
        });
    };

    return (
        <body>
            <Navigation/>
            <div className="content">
            <Header />
            <div className="search-bar">
            <Input
                onPressEnter={handlePressEnter}
                onInputChange={handleInputChange}
                value={inputStr}
            />
            </div>
            <div className="tasks-block">
                <ul>
                    {todoList.map(item => (
                        <TodoItem
                            key={Math.random()}
                            item={item}
                            onChange={handleChangeTodo}
                        />
                    ))}
                    {!todoList.length && (
                        <div className={'no-todo'}>
                            <p>Задач пока нет</p>
                            <img src={NoContent} alt="" />
                        </div>
                    )}
                </ul>
            </div>
            </div>
        </body>
    );
};
